create table cliente
(CLIENTE_ID NUMBER(4) CONSTRAINT CLIENTE_CLIENTE_ID_PK PRIMARY KEY,
cliente varchar2(200) ,
RUC VARCHAR2(11),
DIRECCION  VARCHAR2(200),
departamento VARCHAR2(40));

DROP TABLE CLIENTE;

create table producto
(PRODUCTO_ID NUMBER(4) CONSTRAINT PRODUCTO_PRODUCTO_ID_PK PRIMARY KEY,
producto varchar2(70) ,
PRECIO NUMBER(8,2) ,
CANTIDAD NUMBER (7));

create table tiendas
(TIENDA_ID NUMBER(4) CONSTRAINT TIENDAS_TIENDA_ID_PK PRIMARY KEY,
ZONA VARCHAR2(20),
DIRECCION VARCHAR2 (70),
departamento varchar2(50));

CREATE TABLE DEPARTAMENTO
(DEPARTAMENTO_ID NUMBER(4) CONSTRAINT DEPA_DEPARTAMENTO_ID_PK PRIMARY KEY,
departamento varchar2(50));

CREATE TABLE EMPLEADO
(EMPLEADO_ID NUMBER(5) CONSTRAINT EMPLEADO_EMPLEADO_ID_PK PRIMARY KEY,
NOMBRE VARCHAR2(50),
apellido varchar2(50) ,
fecha_ingreso date,
SALARIO NUMBER(8,2),
comision number(2,3),
admin_dep_id number(5),
TIENDA_ID NUMBER(4) CONSTRAINT EMPLEADO_TIENDA_ID_FK REFERENCES TIENDAS(TIENDA_ID),
departamento_id number(4) constraint empleado_departamento_id_fk references  departamento(departamento_id));

create table venta
(venta_id number(4) constraint venta_venta_id_pk primary key,
tienda_id number(4) constraint venta_tienda_id_fk references tiendas(tienda_id),
EMPLEADO_ID NUMBER(5) CONSTRAINT VENTA_EMPLEADO_ID_FK REFERENCES EMPLEADO(EMPLEADO_ID),
nro_documento varchar2(9),
fecha_ingreso date,
cliente_id number(4) constraint venta_cliente_id_fk references cliente(cliente_id), 
IGV NUMBER(8,2),
TOTAL NUMBER(8,2));

SELECT * FROM VENTA

DROP TABLE VENTA;

create table detalle_venta
(detalle_id number(4) constraint detalle_venta_detalle_id_pk primary key,
venta_id number(4)  constraint detalle_venta_venta_id_fk references venta(venta_id), 
producto_id number(8),
precio number(8,2),
CANTIDAD NUMBER(5),
sub_total number(8,2));

DROP TABLE DETALLE_VENTA

